input = ""

for i in range(len(input) - 4):
    window = input[i : i + 4]

    if (
        (window[0] not in window[1:4])
        and (window[1] not in window[2:4])
        and (window[2] != window[3])
    ):
        print(i + 4)
        break

for i in range(len(input) - 14):
    window = input[i : i + 14]
    sorted_window = sorted(window)

    checker = True
    for j in range(len(sorted_window) - 1):
        if sorted_window[j] == sorted_window[j + 1]:
            checker = False
    if checker == True:
        print(i + 14)
        break
